describe("highlight",function(){it("should allow tagName to be specified",function(){var c="abcde",d="a<span>bcd</span>e",b=a(c);highlight({node:b,pattern:"bcd",tagName:"span"});expect(b.innerHTML).toEqual(d)});it("should allow className to be specified",function(){var c="abcde",d='a<strong class="one two">bcd</strong>e',b=a(c);highlight({node:b,pattern:"bcd",className:"one two"});expect(b.innerHTML).toEqual(d)});it("should be case insensitive by default",function(){var c="ABCDE",d="A<strong>BCD</strong>E",b=a(c);highlight({node:b,pattern:"bcd"});expect(b.innerHTML).toEqual(d)});it("should support case sensitivity",function(){var c="ABCDE",d="ABCDE",b=a(c);highlight({node:b,pattern:"bcd",caseSensitive:true});expect(b.innerHTML).toEqual(d)});it("should support words only matching",function(){var c="tone one phone",d="tone <strong>one</strong> phone",b=a(c);highlight({node:b,pattern:"one",wordsOnly:true});expect(b.innerHTML).toEqual(d)});it("should support matching multiple patterns",function(){var c="tone one phone",d="<strong>tone</strong> one <strong>phone</strong>",b=a(c);highlight({node:b,pattern:["tone","phone"]});expect(b.innerHTML).toEqual(d)});it("should support regex chars in the pattern",function(){var c="*.js when?",d="<strong>*.</strong>js when<strong>?</strong>",b=a(c);highlight({node:b,pattern:["*.","?"]});expect(b.innerHTML).toEqual(d)});it("should work on complex html structures",function(){var c=["<div>abcde","<span>abcde</span>","<div><p>abcde</p></div>","</div>"].join(""),d=["<div><strong>abc</strong>de","<span><strong>abc</strong>de</span>","<div><p><strong>abc</strong>de</p></div>","</div>"].join(""),b=a(c);highlight({node:b,pattern:"abc"});expect(b.innerHTML).toEqual(d)});it("should ignore html tags and attributes",function(){var c='<span class="class"></span>',d='<span class="class"></span>',b=a(c);highlight({node:b,pattern:["span","class"]});expect(b.innerHTML).toEqual(d)});it("should not match across tags",function(){var c="a<span>b</span>c",d="a<span>b</span>c",b=a(c);highlight({node:b,pattern:"abc"});expect(b.innerHTML).toEqual(d)});it("should ignore html comments",function(){var c="<!-- abc -->",d="<!-- abc -->",b=a(c);highlight({node:b,pattern:"abc"});expect(b.innerHTML).toEqual(d)});function a(c){var b=document.createElement("div");b.innerHTML=c;return b}});